import React, { useState } from 'react';
import { Link } from 'react-router-dom';

// Grouped apps by category
const groupedApps = {
  'Accounting / Finance': [
    { id: 'decision-support-new', name: 'Decision Support - New' },
    { id: 'apis-accounts-payable-inquiry-system', name: 'APIS - Accounts Payable Inquiry System' },
    { id: 'accounting-reporting', name: 'Accounting Reporting' },
    { id: 'accounting-scorecard', name: 'Accounting Scorecard' },
    { id: 'claims-management-system', name: 'Claims Management System' },
    { id: 'exchange-rate', name: 'Exchange Rate' },
    { id: 'invoice-match-rate', name: 'Invoice Match Rate' },
    { id: 'producer-portal', name: 'Producer Portal' },
    { id: 'coop-nextgen', name: 'COOP-NextGen' },
    { id: 'co-op-agreement-new', name: 'CO-OP Agreement New' },
    { id: 'dsd-accuracy-dashboard', name: 'DSD Accuracy Dashboard' },
    { id: 'brazil-global-credit', name: 'Brazil Global Credit' }
  ],
  Merchandising: [
    { id: 'online-itemfile', name: 'Online Itemfile' },
    { id: 'item-360', name: 'Item 360' },
    { id: 'sams-item-link', name: "Sam's Item Link" },
    { id: 'modular-planning', name: 'Modular Planning' },
    { id: 'space-management', name: 'Space Management' },
    { id: 'plum', name: 'PLUM' },
    { id: 'active-trading', name: 'Active Trading' },
    { id: 'product-removal', name: 'Product Removal' },
    { id: 'swas-report', name: 'SWAS Report' },
    { id: 'factory-audit-system', name: 'Factory Audit System' },
    { id: 'assortment-planning-execution', name: 'Assortment Planning and Execution' }
  ],
  'Sourcing / Global Procurement': [
    { id: 'global-specifications-supplier-quote', name: 'Global Specifications/Supplier Quote' },
    { id: 'global-trade-document-portal', name: 'Global Trade Document Portal' },
    { id: 'import-po-revisions-new', name: 'Import PO Revisions (New)' },
    { id: 'import-po-delivery', name: 'Import PO Delivery' },
    { id: 'po-tracking', name: 'PO Tracking' },
    { id: 'quote-management', name: 'Quote Management' },
    { id: 'customs-classification', name: 'Customs Classification' },
    { id: 'brand-acquisition-management-system', name: 'Brand Acquisition Management System' }
  ]
  // Add more categories as needed
};

const Apps = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const filteredGroups = Object.entries(groupedApps).reduce((acc, [category, apps]) => {
    const filteredApps = apps.filter(app =>
      app.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    if (filteredApps.length > 0) {
      acc[category] = filteredApps;
    }
    return acc;
  }, {});

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Apps</h1>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search apps..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        className="mb-6 p-2 border rounded w-full max-w-md"
      />

      {/* Grouped App Grid */}
      {Object.entries(filteredGroups).map(([category, apps]) => (
        <div key={category} className="mb-8">
          <h2 className="text-xl font-semibold mb-4">{category}</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            {apps.map(app => (
              <Link
                key={app.id}
                to={`/apps/${app.id}`}
                className="p-4 border rounded shadow hover:bg-gray-50 transition duration-200"
              >
                <strong>{app.name}</strong>
              </Link>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Apps;
