import React from 'react';
import { Link } from 'react-router-dom';
import { ArrowTrendingDownIcon } from '@heroicons/react/24/solid';

const metrics = [
  {
    title: 'Total Sales',
    value: '$3.6K',
    lastYear: '$50.1K',
    percentChange: '-78.39%',
    trend: 'down',
  },
  {
    title: 'Quantity Sold',
    value: '635.00',
    lastYear: '1.6K',
    percentChange: '-62.16%',
    trend: 'down',
  },
  {
    title: 'All Comp Store Sales',
    value: 'N/A',
    lastYear: 'N/A',
    percentChange: 'N/A',
    trend: 'flat',
  },
];

const Business = () => {
  return (
    <div className="p-6 bg-white min-h-screen">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-blue-800">Business</h1>
        <div className="text-sm text-gray-600 text-right">
          <div>
            <span className="font-medium">Supplier Number</span> <span className="text-black">S1 - 217633</span>
          </div>
          <div className="text-xs text-gray-500 mt-1">* Store only data for All Departments as of Jul 11, 2025</div>
        </div>
      </div>

      {/* Tabs */}
      <div className="flex space-x-6 border-b mb-6 text-sm font-medium">
        <div className="border-b-2 border-yellow-500 pb-2 text-gray-900">Card View</div>
        <Link to="/business/graph" className="pb-2 text-gray-500 hover:text-gray-900 hover:border-yellow-500 border-b-2 border-transparent">
          Graph View
        </Link>
      </div>

      {/* Metric Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {metrics.map((metric, idx) => (
          <div key={idx} className="border rounded shadow p-4">
            <div className="text-gray-600 text-sm mb-2">{metric.title}</div>
            <div className="text-3xl font-semibold mb-4">{metric.value}</div>
            <div className="text-xs text-gray-600 space-y-1">
              <div>
                Last Year <span className="ml-2 text-black">{metric.lastYear}</span>
              </div>
              <div>
                % Change <span className="ml-2 text-red-600">{metric.percentChange}</span>
              </div>
              <div className="flex items-center">
                Trend
                {metric.trend === 'down' && <ArrowTrendingDownIcon className="w-4 h-4 ml-2 text-red-500" />}
                {metric.trend === 'flat' && <span className="ml-2 text-gray-500">—</span>}
              </div>
            </div>
            <div className="text-right mt-2">
              <span className="text-blue-700 text-sm cursor-pointer hover:underline">VIEW MORE</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Business;
