import React from 'react';
import { Link } from 'react-router-dom';

const mockApps = [
  { id: 'sales-insights', name: 'Sales Insights' },
  { id: 'inventory-tracker', name: 'Inventory Tracker' },
  { id: 'forecast-tool', name: 'Forecast Tool' }
];

const Apps = () => (
  <div className="p-6">
    <h1 className="text-2xl font-bold mb-4">Apps</h1>
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
      {mockApps.map(app => (
        <Link key={app.id} to={`/apps/${app.id}`} className="p-4 border rounded shadow hover:bg-gray-50">
          <strong>{app.name}</strong>
        </Link>
      ))}
    </div>
  </div>
);

export default Apps;
