import React from 'react';
import {
  DocumentTextIcon,
  ClipboardDocumentIcon,
  FolderIcon,
  ArchiveBoxIcon,
  BookOpenIcon,
  InboxIcon,
  InformationCircleIcon,
} from '@heroicons/react/24/outline';

const docsData = [
  {
    title: 'Store/Club/DC & GLN Lists',
    tooltip: 'Store/Club/DC & GLN Lists',
    icon: DocumentTextIcon,
  },
  {
    title: 'Supplier Replenishment Information',
    tooltip: 'Supplier Replenishment Information',
    icon: ClipboardDocumentIcon,
  },
  {
    title: 'Supplier Guide',
    tooltip: 'Supplier Guide',
    icon: BookOpenIcon,
  },
  {
    title: 'Forms',
    tooltip: 'Forms',
    icon: FolderIcon,
  },
  {
    title: 'Global Sourcing Supplier Administration',
    tooltip: 'Global Sourcing Supplier Administration',
    icon: ArchiveBoxIcon,
  },
  {
    title: 'Order Management System Supplier Training',
    tooltip: 'Order Management System',
    icon: ClipboardDocumentIcon,
  },
  {
    title: 'Global Procurement Information',
    tooltip: 'Global Procurement Information',
    icon: InboxIcon,
  },
  {
    title: 'New DC Sites/Warehouse Realignments',
    tooltip: 'New DC Sites/Warehouse Realignments',
    icon: FolderIcon,
  },
  {
    title: 'Wal-Mart Produce Standards',
    tooltip: 'Wal-Mart Produce Standards',
    icon: InformationCircleIcon,
  },
  {
    title: 'Packaging & Component Disclosure',
    tooltip: 'Packaging & Component Disclosure',
    icon: DocumentTextIcon,
  },
];

const Docs = () => {
  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold mb-6 text-gray-800">Documentation</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {docsData.map((doc, i) => {
          const Icon = doc.icon;
          return (
            <div
              key={i}
              className="bg-white p-4 rounded shadow flex items-center space-x-4 hover:bg-blue-50 transition duration-200 cursor-pointer"
              title={doc.tooltip}
            >
              <Icon className="w-10 h-10 text-blue-600" />
              <div className="text-sm font-medium text-gray-800">
                {doc.title}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Docs;
